package person.test;import org.springframework.beans.factory.FactoryBean;import org.springframework.context.ApplicationContext;import org.springframework.context.support.ClassPathXmlApplicationContext;import person.pojo.Book;/** * 工厂模式 */public class BookFactory implements FactoryBean {    public static void main(String[] args) {        ApplicationContext applicationContext = new ClassPathXmlApplicationContext("test.xml");        Book book = (Book) applicationContext.getBean("bookFactory");        System.out.println(book.toString());    }    @Override    public Object getObject() throws Exception {        System.out.println("获取单例的book");        return Book.getInstance();    }    @Override    public Class<?> getObjectType() {        return Book.class;    }    @Override    public boolean isSingleton() {        return true;    }}