package person.spring.test;import org.springframework.context.ApplicationEvent;import org.springframework.context.ApplicationListener;import org.springframework.core.convert.converter.Converter;import java.util.Date;//spring转化器public class springConverter implements Converter<String ,springTest> {    public  void print(String str){        System.out.println("转化开始============"+str);    }    @Override    public springTest convert(String string) {        print(string);        return new springTest();    }}