package person.spring.test;import org.springframework.aop.framework.AopContext;import org.springframework.context.ApplicationEvent;import org.springframework.context.ApplicationListener;//spring监听器public class springListener implements ApplicationListener {    public  void print(){        System.out.println("监听开始============");    }    @Override    public void onApplicationEvent(ApplicationEvent applicationEvent) {        ((springListener)(AopContext.currentProxy())).print();    }}