package com.yss.acs.todo.common;public enum ProductEnum{    RED("红色",1);    private String color;    private int count;    /**     * @return the color     */    public String getColor(){        return this.color;    }    /**     * @param color the color to set     */    public void setColor(String color){        this.color = color;    }    /**     * @return the count     */    public int getCount(){        return this.count;    }    /**     * @param count the count to set     */    public void setCount(int count){        this.count = count;    }    ProductEnum(String arg0,int arg1){        this.color=arg0;        this.count=arg1;    }}